import { BrowserRouter, Switch, Route} from "react-router-dom";
import HomePage from "./pages/HomePage";
import MovieDetailComponent from "./components/MovieDetailComponent";
import FavoriteMovieListPage from "./pages/FavoriteMovieListPage";
import LoginComponent from "./components/LoginComponent"
import React, { Component } from "react";
import Recommended from "./components/RecommendComponent"

export default class App extends Component {
  constructor() {
    super();
    this.state = {
      loggedInStatus: "NOT_LOGGED_IN",
      user: {}
    }
    this.handleSuccessfulLogin = this.handleSuccessfulLogin.bind(this);
  }

  componentWillMount(){
    var status = localStorage.getItem('Status');
    var info = localStorage.getItem('User')
    if (status != null && info != null){
      this.setState({loggedInStatus: status})
      this.setState({user: info})
    }
  }

  handleSuccessfulLogin(data){
    this.setState({loggedInStatus: "LOGGED_IN"})
    this.setState({user: data.user})
    localStorage.setItem('Status', "LOGGED_IN")
    localStorage.setItem('User', data.user)
  }

  render() {
    return (
      <div className="App">
        <div className="container">
    <h3>STATUS: {this.state.loggedInStatus}</h3>
        <BrowserRouter>
          <Switch>
            <Route path="/" exact render={(props) => <HomePage {...props} state={this.state} />}></Route>
            <Route path="/home" exact render={(props) => <HomePage {...props} state={this.state} />}></Route>
            <Route
              path="/:likedMovie"
              exact
              render={(props) => <FavoriteMovieListPage {...props} state={this.state}/>}
            ></Route>
            <Route
              path="/movie/:id"
              exact
              component={MovieDetailComponent}
            ></Route>
            <Route
              path={"/login/form"}
              exact
              render={(props) => <LoginComponent {...props} loggedInStatus={this.state.loggedInStatus} handleSuccessfulLogin={this.handleSuccessfulLogin} />}
            ></Route>
            <Route
              path={"/recommend/movies"}
              exact
              render={(props) => <Recommended {...props} state={this.state} handleSuccessfulLogin={this.handleSuccessfulLogin} />}
            ></Route>
          </Switch>
        </BrowserRouter>
        </div>
      </div>
    );
  }
}
