import React, { Component } from "react";
import {Router, Switch, Route, withRouter} from "react-router-dom";
import HomePage from "../pages/HomePage";
import MovieDetailComponent from "./MovieDetailComponent";
import FavoriteMovieListPage from "../pages/FavoriteMovieListPage";
import LoginComponent from "./LoginComponent"
import history from './history';

class Header extends Component {
  constructor(props) {
    super(props)
    this.handleSuccessfulLogin = this.handleSuccessfulLogin.bind(this)
    this.state = {
      loggedInStatus: "NOT_LOGGED_IN",
      user: {}
    }
  }

  handleSuccessfulLogin(){
    this.props.history.push("/");
  }

  render() {
    return (
      <div className="container">
        <Router history={history}>
          <header className="blog-header py-3">
            <div className="row flex-nowrap justify-content-between align-items-center">
              <div className="col-4 pt-1">
                <a className="text-muted" href="/">
                  Cinema Store
                </a>
              </div>
            </div>

            <div className="row flex-nowrap justify-content-between align-items-center">
              <div className="col-4 pt-1">
                <a className="text-muted" href="/login/form">
                  Log In
                </a>
              </div>
            </div>

            <div className="flex-nowrap justify-content-between align-items-center mt-3">
              <nav className="navbar navbar-expand-md">
                <div
                  className="row collapse navbar-collapse"
                  id="navbarCollapse"
                >
                  <ul className="navbar-nav mr-auto">
                    <li className="nav-item btn btn-light">
                      <a className="navbar-brand mb-0 h1" href="/home">
                        Movies List <span className="sr-only">(current)</span>
                      </a>
                    </li>
                    <li className="nav-item btn btn-light">
                      <a className="navbar-brand mb-0 h1" href="/likedMovie">
                        Favorite Movie
                      </a>
                    </li>
                  </ul>
                </div>
              </nav>
            </div>
          </header>
          <Switch>
            <Route path="/" exact component={HomePage}></Route>
            <Route path="/home" exact component={HomePage}></Route>
            <Route
              path="/:likedMovie"
              exact
              render={(props) => <FavoriteMovieListPage {...props} loggedInStatus={this.state.loggedInStatus} />}
            ></Route>
            <Route
              path="/movie/:id"
              exact
              component={MovieDetailComponent}
            ></Route>
            <Route
              path={"/login/form"}
              exact
              render={(props) => <LoginComponent {...props} loggedInStatus={this.state.loggedInStatus} handleSuccessfulLogin={() => this.handleSuccessfulLogin()} />}
            ></Route>
          </Switch>
        </Router>
      </div>
    );
  }
}
export default withRouter(Header);
