import React, { Component } from "react";
import Header from "../components/header";
import MovieItemComponent from "../components/MovieItemComponent";
import Footer from "../components/Footer";
import axios from "axios"

class FavoriteMovieListPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listMovies: [
        { name: "Joker (2019)",
                    description:
                    "In Gotham City, mentally troubled comedian Arthur Fleck is disregarded and mistreated by society....",
                    rateScore: 4,
                    actors: [],
                    releaseDate: "21 February 2019 (Vietnam)",
                    imageUrl:
                    "https://m.media-amazon.com/images/M/MV5BNGVjNWI4ZGUtNzE0MS00YTJmLWE0ZDctN2ZiYTk2YmI3NTYyXkEyXkFqcGdeQXVyMTkxNjUyNQ@@._V1_UX182_CR0,0,182,268_AL_.jpg",
                    isLiked: false,}
      ],
      user: this.props.state.user,
      loggedInStatus: this.props.state.loggedInStatus
    };
    this.showListMovieLiked = this.showListMovieLiked.bind(this);
  }
  componentWillMount() {
    // axios.get("http://localhost:5000/api/recommend/1", (req, res) => {
    //       if (res.data.status === "success") {
    //           this.setState({
    //               listMovies: res.data.list,
    //           });
    //       }
    //   })
    let list = [];
    list = JSON.parse(localStorage.getItem("list"));
    // this.setState({
    //   listMovies: list,
    // });
  }

  onChangeStatus = (id) => {
    console.log("movie id:" + id);
    this.state.listMovies.map((item, index) => {
      if (index === id) {
        item.isLiked = !item.isLiked;
        console.log("status: " + item.isLiked);
        // this.state.listMovies[id] = item;
        // this.setState({
        //   listMovies: this.state.listMovies,
        // });

        localStorage.setItem("list", JSON.stringify(this.state.listMovies));
      }
    });
  };
  showListMovieLiked(listMovies) {
    return (
      <div>
        {listMovies != null
          ? listMovies.map((movie, index) => {
              if (movie.isLiked)
                return (
                  <MovieItemComponent
                    key={index}
                    id={index}
                    name={movie.name}
                    description={movie.description}
                    rateScore={movie.rateScore}
                    actors={movie.actors}
                    releaseDate={movie.releaseDate}
                    imageUrl={movie.imageUrl}
                    isLiked={movie.isLiked}
                    onUpdateStatus={this.onChangeStatus}
                    onChange={this.onChangeStatus}
                  />
                );
            })
          : ""}
      </div>
    );
  }
  render() {
    return (
      <div>
        <Header state={this.props.state}/>
        <div>
          {this.props.match.params.likedMovie === "likedMovie"
            ? this.showListMovieLiked(this.state.listMovies)
            : null}
        </div>
        <Footer />
      </div>
    );
  }
}

export default FavoriteMovieListPage;
